import React, { Component } from 'react';
import App from './App';

class StatusForm extends Component {
  constructor(props) {
    super(props);
    this.state = {
      formValue: ''
    }
  }

  handleFormSubmit = (event) => {
    console.log('form submit function called');
    event.preventDefault();
    console.log(this.state.formValue);
  }

  updateState = (event) => {
    // update the state
    // push the texcontent inside of a state array that is meant to store all posts
    // and clear the input box
    // map through it 
    // create a new post
    
    this.setState({ formValue : event.target.value});
    event.target.value = '';
  }

  render() {
    // we have 2 functions
    // the first one blocks the submit 
    // the second one updates the value of the state with the
    // value of the textBox
    return (
      <div className='status-box'>
        <h4>Create a post</h4>
        <span><h4>{this.props.currentUser}</h4></span>

        <form className='input-form' onSubmit={this.handleFormSubmit}> 
            <textarea value={this.state.formValue}  onChange={this.updateState} placeholder='Write a post...' className='status-post-box'/>
            <input type='submit' value='Post' className='submit-button'></input>
        </form>
      </div>

    )
  }
}

export default StatusForm;
