import React, { Component } from 'react';
import App from './App';

class StatusForm extends Component {
  constructor(props) {
    super(props);

  }

  handleFormSubmit = (event) => {
    console.log('form submit function called');
    event.preventDefault();
    console.log(event.target)
  }

  render() {
    return (
      <div className='status-box'>
        <h4>Create a post</h4>
        <span><h4>{this.props.currentUser}</h4></span>

        <form className='input-form' onSubmit={this.handleFormSubmit}> 
            <input type='text' placeholder='Write a post...' className='status-post-box'></input>
            <input type='button' value='Post' className='submit-button'></input>
        </form>
      </div>

    )
  }
}

export default StatusForm;
