import React, { Component } from 'react';
//import Header from './Header';
//import Status from './Status';
import Timeline from './Timeline';
import Post from './Post';

// import Comment from './Comment';
import './App.css';

// current functionality objectives
// link my current user to my post
// be able to post an item according to each user

class App extends Component {
  constructor (props) {
    super(props);
    this.state = {
      currentUser: 'Jawad',
      users: ['jawad', 'Skywalker'],
      userPosts: [],

      formValue: ''
    }
  }

  createPost = (event) => {
    // in order to create a new post, 
    // when we click on the post button,
    // it should create a new object that contains the post message
    // the name of the poster and the time. 

    console.log('Status form OnChange lead to this function');
    console.log(event.target.value);
  }

  handleFormSubmit = (event) => {
    console.log('form submit function called');
    event.preventDefault();
    console.log(event.target.textContent)
  }

  updateCurrentUser = (event) => {
    {/* inside of header, on change , when the name is selected, grab it with event.name and change state to it */}
    console.log('value of this.state.currentUser before this.setState' + this.state.currentUser);
    let selectedUser = event.target.value;
    console.log('the select tag has updated, called props.onChange which called updateCurrentUser')
    this.setState({ currentUser : selectedUser});
    console.log('value of this.state.currentUser after this.setState ' + this.state.currentUser);
  }

  render() {

    return (
      <div className='main-container'>
        <Header currentUser={this.state.currentUser} users={this.state.users} onChange={this.updateCurrentUser}/>
        <StatusForm currentUser={this.state.currentUser} users={this.state.users} handleFormSubmit={this.handleFormSubmit} formValue={this.state.formValue} onClick={this.createPost} />
        <Timeline />
      </div>

    )
  }
}

let Header = (props) => {
  let allUsers = props.users;

  return (
    <div className='header'>
      <h2>Social News Feed</h2>
      <select className='user-select-list' onChange={props.onChange}>
        {allUsers.map(user => { return <option value={user}>{user}</option> })}
      </select>
    </div>
  )
}

let StatusForm = () => {
  return (
    <div className='status-box'>
      <h4>Create a post</h4>
      <span><h4>{this.props.currentUser}</h4></span>

      <form className='input-form' onSubmit={this.props.handleFormSubmit}> 
          <input type='text' placeholder='Write a post...' className='status-post-box'></input>
          <input type='button' value='Post' className='submit-button'></input>
      </form>
    </div>
  )
}

export default App;
