import React, { Component } from 'react';
//import Header from './Header';
import Status from './Status';
import Timeline from './Timeline';
import Post from './Post';

// import Comment from './Comment';
import './App.css';

class App extends Component {
  constructor (props) {
    super(props);
    this.state = {
      currentUser: 'jawad',
      users : ['jawad', 'johnathan'],
      userPosts : []
    }
  }

  createPost = (event) => {
    console.log('hi');
  }

  render() {
    return (
      <div className='main-container'>
        <Header currentUser={this.state.currentUser} users={this.state.users}/>
        <Status currentUser={this.state.currentUser} users={this.state.users} onClick={this.createPost}/>
        <Timeline />
      </div>

    )
  }
}

let Header = () => {
  return (
    <div className='header'>
      <h2>Social News Feed</h2>
      <select className='user-select-list'>
        {this.props.users.map(user => { return <option value={user}>{user}</option>})}
      </select>
    </div>
  )
}
export default App;
