import React, { Component } from 'react';
import App from './App';

class Status extends Component {
  constructor(props) {
    super(props);
  }

  render() {
    return (
      <div className='status-box'>
        <h4>Create a post</h4>
        <span><h4>{this.props.currentUser}</h4></span>

        <form className='input-form' onSubmit={this.props.handleFormSubmit}> 
            <input type='text' placeholder='Write a post...' className='status-post-box' value={this.props.formValue}></input>
            <input type='button' value='Post' className='submit-button'  ></input>
        </form>
      </div>

    )
  }
}

export default Status;
